/*global tinymce:true */

(function() {
	tinymce.create('tinymce.plugins.BBCodePlugin', {
		init : function(ed) {
			var t = this, dialect = ed.getParam('bbcode_dialect', 'punbb').toLowerCase();

			ed.on('beforeSetContent', function(e) {
				e.content = t['_' + dialect + '_bbcode2html'](e.content);
			});

			ed.on('postProcess', function(e) {
				if (e.set) {
					e.content = t['_' + dialect + '_bbcode2html'](e.content);
				}

				if (e.get) {
					e.content = t['_' + dialect + '_html2bbcode'](e.content);
				}
			});
		},

		getInfo: function() {
			return {
				longname: 'BBCode Plugin',
				author: 'Moxiecode Systems AB',
				authorurl: 'http://www.tinymce.com',
				infourl: 'http://www.tinymce.com/wiki.php/Plugin:bbcode'
			};
		},

		// Private methods

		// HTML -> BBCode in ModernBB dialect
		_punbb_html2bbcode : function(s) {
			s = tinymce.trim(s);

			function rep(re, str) {
				s = s.replace(re, str);
			}

			// example: <strong> to [b]
			rep(/<a.*?href=\"(.*?)\".*?>(.*?)<\/a>/gi,"[url=$1]$2[/url]");
			rep(/<font.*?color=\"(.*?)\".*?class=\"quoteStyle\".*?>(.*?)<\/font>/gi,"[quote][color=$1]$2[/color][/quote]");
			rep(/<font.*?class=\"quoteStyle\".*?color=\"(.*?)\".*?>(.*?)<\/font>/gi,"[quote][color=$1]$2[/color][/quote]");
			rep(/<span style=\"color: ?(.*?);\">(.*?)<\/span>/gi,"[color=$1]$2[/color]");
			rep(/<font.*?color=\"(.*?)\".*?>(.*?)<\/font>/gi,"[color=$1]$2[/color]");
			rep(/<span style=\"font-size:(.*?);\">(.*?)<\/span>/gi,"[size=$1]$2[/size]");
			rep(/<span style=\"text-decoration: line-through;\">(.*?)<\/span>/gi,"[s]$1[/s]");
			rep(/<font>(.*?)<\/font>/gi,"$1");
			rep(/<img src=\"(.*?)\" .*?alt="(.*?)".*? \/>/gi,"[img]$1[/img]");
			rep(/<pre>(.*?)<\/pre>/gi,"[code]$1[/code]");
			rep(/<code>(.*?)<\/code>/gi,"[c]$1[/c]");
			rep(/<blockquote>(.*?)<footer><cite>(.*?)<\/cite><\/footer><\/blockquote>/gi,"[quote=$2]$1[/quote]");
			rep(/<blockquote>(.*?)<\/blockquote>/gi,"[quote]$1[/quote]");
			rep(/<strong class=\"quoteStyle\">(.*?)<\/strong>/gi,"[quote][b]$1[/b][/quote]");
			rep(/<em class=\"quoteStyle\">(.*?)<\/em>/gi,"[quote][i]$1[/i][/quote]");
			rep(/<u class=\"quoteStyle\">(.*?)<\/u>/gi,"[quote][u]$1[/u][/quote]");
			rep(/<ol style=\"list-style-type: lower-alpha;\">/gi,"[list=a]");
			rep(/<p style=\"text-align: center;\">(.*?)<\/p>/gi,"[center]$1[/center]");
			rep(/<p style=\"text-align: right;\">(.*?)<\/p>/gi,"[right]$1[/right]");
			rep(/<p style=\"text-align: justify;\">(.*?)<\/p>/gi,"[justify]$1[/justify]");
			rep(/<\/(ol|ul)>/gi,"[/list]");
			rep(/<ol>/gi,"[list=1]");
			rep(/<ul>/gi,"[list=*]");
			rep(/<\/(strong|b)>/gi,"[/b]");
			rep(/<(strong|b)>/gi,"[b]");
			rep(/<\/(em|i)>/gi,"[/i]");
			rep(/<(em|i)>/gi,"[i]");
			rep(/<\/u>/gi,"[/u]");
			rep(/<h1>/gi,"[h1]");
			rep(/<\/h1>/gi,"[/h1]");
			rep(/<h2>/gi,"[h2]");
			rep(/<\/h2>/gi,"[/h2]");
			rep(/<h3>/gi,"[h3]");
			rep(/<\/h3>/gi,"[/h3]");
			rep(/<h4>/gi,"[h4]");
			rep(/<\/h4>/gi,"[/h4]");
			rep(/<h5>/gi,"[h5]");
			rep(/<\/h5>/gi,"[/h5]");
			rep(/<h6>/gi,"[h6]");
			rep(/<\/h6>/gi,"[/h6]");
			rep(/<sub>/gi,"[sub]");
			rep(/<\/sub>/gi,"[/sub]");
			rep(/<sup>/gi,"[sup]");
			rep(/<\/sup>/gi,"[/sup]");
			rep(/<li>/gi,"[\*]");
			rep(/<\/li>/gi,"[/\*]");
			rep(/<span style=\"text-decoration: ?underline;\">(.*?)<\/span>/gi,"[u]$1[/u]");
			rep(/<u>/gi,"[u]");
			rep(/<blockquote[^>]*>/gi,"[quote]");
			rep(/<\/blockquote>/gi,"[/quote]");
			rep(/<p>/gi,"");
			rep(/<\/p>/gi,"\n");
			rep(/&nbsp;|\u00a0/gi," ");
			rep(/&quot;/gi,"\"");
			rep(/&lt;/gi,"<");
			rep(/&gt;/gi,">");
			rep(/&amp;/gi,"&");
			rep(/<br \/>/gi,"\n");
			rep(/<br\/>/gi,"\n");
			rep(/<br>/gi,"\n");
			// Emoticons from BBCode img to BBCode
			rep(/\[img\]img\/smilies\/smile.png\[\/img\]/gi,":)");
			rep(/\[img\]img\/smilies\/neutral.png\[\/img\]/gi,":|");
			rep(/\[img\]img\/smilies\/sad.png\[\/img\]/gi,":(");
			rep(/\[img\]img\/smilies\/big_smile.png\[\/img\]/gi,":D");
			rep(/\[img\]img\/smilies\/yikes.png\[\/img\]/gi,":o");
			rep(/\[img\]img\/smilies\/wink.png\[\/img\]/gi,";)");
			rep(/\[img\]img\/smilies\/hmm.png\[\/img\]/gi,":/");
			rep(/\[img\]img\/smilies\/tongue.png\[\/img\]/gi,":p");
			rep(/\[img\]img\/smilies\/lol.png\[\/img\]/gi,":lol:");
			rep(/\[img\]img\/smilies\/mad.png\[\/img\]/gi,":@");
			rep(/\[img\]img\/smilies\/roll.png\[\/img\]/gi,"%)");
			rep(/\[img\]img\/smilies\/cool.png\[\/img\]/gi,":cool:");
			rep(/\[img\]img\/smilies\/happycry.png\[\/img\]/gi,":hc:");
			rep(/\[img\]img\/smilies\/angel.png\[\/img\]/gi,"(A)");
			rep(/\[img\]img\/smilies\/ohyeah.png\[\/img\]/gi,"^-^");
			rep(/\[img\]img\/smilies\/happy.png\[\/img\]/gi,"^.^");
			// Emoticons from HTML to BBCode
			rep(/<img src="img\/smilies\/smile.png" \/>&nbsp;/gi,":)");
			rep(/<img src="img\/smilies\/neutral.png" \/>&nbsp;/gi,":|");
			rep(/<img src="img\/smilies\/sad.png" \/>&nbsp;/gi,":(");
			rep(/<img src="img\/smilies\/big_smile.png" \/>&nbsp;/gi,":D");
			rep(/<img src="img\/smilies\/yikes.png" \/>&nbsp;/gi,":o");
			rep(/<img src="img\/smilies\/wink.png" \/>&nbsp;/gi,";)");
			rep(/<img src="img\/smilies\/hmm.png" \/>&nbsp;/gi,"(:/)");
			rep(/<img src="img\/smilies\/tongue.png" \/>&nbsp;/gi,":p");
			rep(/<img src="img\/smilies\/lol.png" \/>&nbsp;/gi,":lol:");
			rep(/<img src="img\/smilies\/mad.png" \/>&nbsp;/gi,":@");
			rep(/<img src="img\/smilies\/roll.png" \/>&nbsp;/gi,"%)");
			rep(/<img src="img\/smilies\/cool.png" \/>&nbsp;/gi,":cool:");
			rep(/<img src="img\/smilies\/happycry.png" \/>&nbsp;/gi,":hc:");
			rep(/<img src="img\/smilies\/angel.png" \/>&nbsp;/gi,"(A)");
			rep(/<img src="img\/smilies\/ohyeah.png" \/>&nbsp;/gi,"^-^");
			rep(/<img src="img\/smilies\/happy.png" \/>&nbsp;/gi,"^.^");

			return s;
		},

		// BBCode -> HTML from ModernBB dialect
		_punbb_bbcode2html : function(s) {
			s = tinymce.trim(s);

			function rep(re, str) {
				s = s.replace(re, str);
			}

			// example: [b] to <strong>
			rep(/\n/gi,"<br />");
			rep(/\[b\]/gi,"<strong>");
			rep(/\[\/b\]/gi,"</strong>");
			rep(/\[i\]/gi,"<em>");
			rep(/\[\/i\]/gi,"</em>");
			rep(/\[ins\]/gi,"<ins>");
			rep(/\[\/ins\]/gi,"</ins>");
			rep(/\[u\]/gi,"<u>");
			rep(/\[\/u\]/gi,"</u>");
			rep(/\[h1\]/gi,"<h1>");
			rep(/\[\/h1\]/gi,"</h1>");
			rep(/\[h2\]/gi,"<h2>");
			rep(/\[\/h2\]/gi,"</h2>");
			rep(/\[h3\]/gi,"<h3>");
			rep(/\[\/h3\]/gi,"</h3>");
			rep(/\[h4\]/gi,"<h4>");
			rep(/\[\/h4\]/gi,"</h4>");
			rep(/\[h5\]/gi,"<h5>");
			rep(/\[\/h5\]/gi,"</h5>");
			rep(/\[h6\]/gi,"<h6>");
			rep(/\[\/h6\]/gi,"</h6>");
			rep(/\[del\]/gi,"<del>");
			rep(/\[\/del\]/gi,"</del>");
			rep(/\[sub\]/gi,"<sub>");
			rep(/\[\/sub\]/gi,"</sub>");
			rep(/\[sup\]/gi,"<sup>");
			rep(/\[\/sup\]/gi,"</sup>");
			rep(/\[\*\]/gi,"<li>");
			rep(/\[\/\*\]/gi,"</li>");
			rep(/\[list=a\]/gi,"<ol style=\"list-style-type: lower-alpha\">");
			rep(/\[list=1\]/gi,"<ol>");
			rep(/\[list]/gi,"<ul>");
			rep(/\[list=\*\]/gi,"<ul>");
			rep(/\[\/list\]/gi,"</ol>");
			rep(/\[center\](.*?)\[\/center\]/gi,"<p style=\"text-align: center;\">$1</p>");
			rep(/\[right\](.*?)\[\/right\]/gi,"<p style=\"text-align: right;\">$1</p>");
			rep(/\[justify\](.*?)\[\/justify\]/gi,"<p style=\"text-align: justify;\">$1</p>");
			rep(/\[s\](.*?)\[\/s\]/gi,"<span style=\"text-decoration: line-through;\">$1</span>");
			rep(/\[url=([^\]]+)\](.*?)\[\/url\]/gi,"<a href=\"$1\">$2</a>");
			rep(/\[url\](.*?)\[\/url\]/gi,"<a href=\"$1\">$1</a>");
			rep(/\[img=([^\]]+)\]([^\]]+)\[\/img\]/gi,"<img src=\"$2\" alt=\"$1\" />");
			rep(/\[img]([^\]]+)\[\/img\]/gi,"<img src=\"$1\" />");
			rep(/\[color=(.*?)\](.*?)\[\/color\]/gi,"<font color=\"$1\">$2</font>");
			rep(/\[code\](.*?)\[\/code\]/gi,"<pre>$1</pre>&nbsp;");
			rep(/\[c\](.*?)\[\/c\]/gi,"<code>$1</code>&nbsp;");
			rep(/\[quote\](.*?)\[\/quote\]/gi,"<blockquote>$1</blockquote>&nbsp;");
			rep(/\[quote=(.*?)\](.*?)\[\/quote\]/gi,"<blockquote>$2<footer><cite>$1</cite></footer></blockquote>&nbsp;");
			rep(/<br \/>/gi,"\n");
			rep(/<br\/>/gi,"\n");
			rep(/<br>/gi,"\n");
			rep(/<p>/gi,"");
			rep(/<\/p>/gi,"\n");
			rep(/&nbsp;|\u00a0/gi," ");
			rep(/&quot;/gi,"\"");
			rep(/&lt;/gi,"<");
			rep(/&gt;/gi,">");
			rep(/&amp;/gi,"&");
			// Emoticons from BBCode to HTML
			rep(/\:\)/gi,"<img src=\"img/smilies/smile.png\" />&nbsp;");
			rep(/\=\)/gi,"<img src=\"img/smilies/smile.png\" />&nbsp;");
			rep(/\:\|/gi,"<img src=\"img/smilies/neutral.png\" />&nbsp;");
			rep(/\=\|/gi,"<img src=\"img/smilies/neutral.png\" />&nbsp;");
			rep(/\:\(/gi,"<img src=\"img/smilies/sad.png\" />&nbsp;");
			rep(/\=\(/gi,"<img src=\"img/smilies/sad.png\" />&nbsp;");
			rep(/\:D/gi,"<img src=\"img/smilies/big_smile.png\" />&nbsp;");
			rep(/\=D/gi,"<img src=\"img/smilies/big_smile.png\" />&nbsp;");
			rep(/\:o/gi,"<img src=\"img/smilies/yikes.png\" />&nbsp;");
			rep(/\:O/gi,"<img src=\"img/smilies/yikes.png\" />&nbsp;");
			rep(/\;\)/gi,"<img src=\"img/smilies/wink.png\" />&nbsp;");
			rep(/\(:\/\)/gi,"<img src=\"img/smilies/hmm.png\" />&nbsp;");
			rep(/\:p/gi,"<img src=\"img/smilies/tongue.png\" />&nbsp;");
			rep(/\:P/gi,"<img src=\"img/smilies/tongue.png\" />&nbsp;");
			rep(/\:lol:/gi,"<img src=\"img/smilies/lol.png\" />&nbsp;");
			rep(/\:\)\)/gi,"<img src=\"img/smilies/lol.png\" />&nbsp;");
			rep(/\:-\)\)/gi,"<img src=\"img/smilies/lol.png\" />&nbsp;");
			rep(/\:@/gi,"<img src=\"img/smilies/mad.png\" />&nbsp;");
			rep(/\:mad:/gi,"<img src=\"img/smilies/mad.png\" />&nbsp;");
			rep(/\%\)/gi,"<img src=\"img/smilies/roll.png\" />&nbsp;");
			rep(/\:rolleyes:\)/gi,"<img src=\"img/smilies/roll.png\" />&nbsp;");
			rep(/\:cool:/gi,"<img src=\"img/smilies/cool.png\" />&nbsp;");
			rep(/b:/gi,"<img src=\"img/smilies/cool.png\" />&nbsp;");
			rep(/B:/gi,"<img src=\"img/smilies/cool.png\" />&nbsp;");
			rep(/\:hc:/gi,"<img src=\"img/smilies/happycry.png\" />&nbsp;");
			rep(/\(A\)/gi,"<img src=\"img/smilies/angel.png\" />&nbsp;");
			rep(/\(a\)/gi,"<img src=\"img/smilies/angel.png\" />&nbsp;");
			rep(/\^-\^/gi,"<img src=\"img/smilies/ohyeah.png\" />&nbsp;");
			rep(/\^.\^/gi,"<img src=\"img/smilies/happy.png\" />&nbsp;");

			return s;
		}
	});

	// Register plugin
	tinymce.PluginManager.add('bbcode', tinymce.plugins.BBCodePlugin);
})();